import React from "react";
import {useState} from "react";

import LocationItem from "../components/location-item/LocationItem";


const FullScreen = props =>{

    // console.log('im in full screen component');
    
    // const userId = useParams().userId;
    const [fullImage, setOff] = useState(true);

    const exitImage = () => {
        console.log('im in exitImage Func ');
        setOff(false);
    }

    if(!fullImage){
        return (
            <LocationItem  
                    key={props.value.id}
                    id={props.value.id}
                    image={props.value.image}
                    title={props.value.title}
                    description={props.value.description}
                    address={props.value.address}
                    creatorId={props.value.creator}
                    coordinates={props.value.coordinates}   
                />
        );
    }
    
    return (
        <React.Fragment>
           <main onClick={exitImage}>
                <button style={{marginLeft: "850px"}}>X</button>
                <img src={props.value.image}  alt={props.value.title} width="600px" height="550px"/>
            </main> 
        </React.Fragment>
    );
}

export default FullScreen;
