import React, {useState} from "react";
import { Link, useParams } from "react-router-dom";
const FullScreen = props =>{

    console.log('im in full screen component');
    
    const userId = useParams().userId;
    const [fullImage, setOff] = useState(true);

    const exitImage = () => {
        console.log('im in exitImage Func user id: ' +  userId);
        setOff(false);
    }
    
    return (
        <React.Fragment>
            {!fullImage && <Link to={`/${userId}/places`} />}
        
            {fullImage &&
            <div onClick={exitImage}>
                <button style={{marginLeft: "850px"}}>X</button>
                <img src={props.imgUrl}  alt={props.title}/>
            </div>
            }
        </React.Fragment>
    );
}

export default FullScreen;
