//         },
//         creator: 'u1'
//     },
//     {
//         id: 'p2',
//         title: 'Empire State Building',
//         description: 'One of the most famous buildings in the world',
//         imageUrl: 'https://embriture.org/wp-content/uploads/2021/09/Burj-portrait-lagre_tcm25-475749.jpg',
//         address: '1 Sheikh Mohammed bin Rashid Blvd - Downtown Dubai - Dubai - United Arab Emirates',
//         coordinates: {
//             lat: 25.1972018,
//             lng: 55.2721824
//         },
//         creator: 'u2'
//     },
//     {
//         id: 'p3',
//         title: 'Empire State Building',
//         description: 'One of the most famous buildings in the world',
//         imageUrl: 'https://embriture.org/wp-content/uploads/2021/09/Burj-portrait-lagre_tcm25-475749.jpg',
//         address: '1 Sheikh Mohammed bin Rashid Blvd - Downtown Dubai - Dubai - United Arab Emirates',
//         coordinates: {
//             lat: 25.1972018,
//             lng: 55.2721824
//         },
//         creator: 'u2'
//     },
// ];

const EditLocation = () => {
    const {isLoading, error, sendRequest, clearError} = useHttpClient();
    const [laodedPlasce, setLoadedPlace] = useState();
    const placeId = useParams().placeId;

    
    const [formState, inputHandler, setFormData] = useForm({
        title: {
            value: '',
            isValid: false
        },
        description: {
            value: '',
            isValid: false
        }
    }, false);

    useEffect(() => {
        const fetchPlace = async () => {
            try {
                const responseData = await sendRequest(
                    `http://localhost:5000/api/places/${placeId}`
                );
                setLoadedPlace(responseData.place);
                setFormData({
                    title: {
                        value: responseData.place.title,
                        isValid: true
                    },
                    description: {
                        value: responseData.place.description,
                        isValid: true
                    }
                }, true);     
            } catch (error) { }
        } 
        fetchPlace();
    }, [sendRequest, placeId, setFormData]);


    const submitEditHandler = event => {
        event.preventDefault();
        console.log(formState.inputs);
    }

    if(!laodedPlasce && !error){
        return (
        <div className="center">
            <Card>
                <h2>Could not find the location </h2>
            </Card>
        </div>);
    }


    return (
        <React.Fragment>
            <ErrorModal error={error} onCleaar={clearError} />
            {isLoading && 
            <div className="center">
                <LoadingSpinner  asOverlay/>
            </div> }
            {!isLoading && laodedPlasce &&
            <form className="place-form" onSubmit={submitEditHandler}>
                <Input 
                    id="title"
                    element="input"
                    type="text"
                    label="Title"
                    validators={[VALIDATOR_REQUIRE()]}
                    errorText="Enter a valid title"
                    onInput={inputHandler}
                    initialValue={formState.inputs.title.value}
                    initialValid={formState.inputs.title.isValid} 
                />
                <Input 
                    id="description"
                    element="textarea"
                    label="Description"
                    validators={[VALIDATOR_MINLENGTH(5)]}
                    errorText="Enter a valid description! min 5 characters"
                    onInput={inputHandler}
                    initialValue={formState.inputs.description.value}
                    initialValid={formState.inputs.description.isValid} 
                />
                <Button type="submit" disabled={!formState.isValid}>Update Location</Button>
                <Button danger to="/users"> Cencel </Button>
            </form>}
        </React.Fragment>
    );
}

export default EditLocation;
