import React, {useState, useCallback,useEffect} from "react";
import { useLocation } from "react-router-dom";

import Button from "../../../shared/components/FormElements/Button";
import Card from "../../../shared/components/UiElements/Card";
import LocationItem from "../location-item/LocationItem";
import "./LocationList.css";

const LocationList = props => {

  const [isHomePage, setHomePage] = useState();
  
  const currPath = useLocation();


  

  useEffect(() => {
    if(currPath.pathname === "/"){
      setHomePage(true);
      console.log("expected: / and recived:  ",currPath);
    }
    else{
      setHomePage(false);
      console.log("expected: someUrl and recived:  ",currPath);
    }
  }, );

  if(props.items.length === 0){
      return (
          <div className="place-list center">
              <Card>
                  <h2>No Location Found maybe create on?</h2>
                  <Button to="/places/new"> Share a Place</Button>
              </Card>
          </div>
      );
  }

  return (
    <React.Fragment>
      <div> 
        {
          !isHomePage  &&
          <section >
            <Button inverse to="/users">Go Back</Button>
          </section>
        }
        
        <ul className="place-list">
          {props.items.map((location) => {
            return (
              <LocationItem
                key={location.id}
                id={location.id}
                image={location.imageUrl}
                title={location.title}
                description={location.description}
                address={location.address}
                creatorId={location.creator}
                coordinates={location.coordinates}
              />
            );
          })}
        </ul>
      </div>
    </React.Fragment>
  );
}

export default LocationList;
