import React, {useState} from "react";

import "./LocationItem.css"
import Card from "../../../shared/components/UiElements/Card"
import Button from "../../../shared/components/FormElements/Button";
import FullScreen from "../../pages/FullScreen";
import ModalWindow from "../../../shared/components/UiElements/ModalWindow";

const LocationItem = props => {
  
  const [showMap,setShowMap] = useState(false);
  const [isFullScreen, setFullscreen] = useState(false);

  const openMapHandler = () => setShowMap(true);
  const closeMapHandler = () => setShowMap(false);

  const showFullScreen = () => setFullscreen(true);

  return (
    <React.Fragment>
      <ModalWindow 
        show={showMap}
        onCancel={closeMapHandler}
        header={props.address}
        contentClass="place-item__modal-content"
        footerClass="place-item__modal-actions"
        footer={<Button onClick={closeMapHandler}>Close</Button>}
      >
        <div className="map-container">
          <h2>The Map</h2>
        </div>
      </ModalWindow>
      {isFullScreen && 
        <FullScreen value={props}/>
      }
      {!isFullScreen &&
      <li className="place-item">
        <Card className="place-item__content">
          <div className="place-item__image" onClick={showFullScreen} >
            <img src={props.image} alt={props.title} />
          </div>
          <div className="place-item__info">
            <h2>{props.title}</h2>
            <h3>{props.address}</h3>
            <p>{props.ddescription}</p>
          </div>
          <div className="place-item__actions">
            <Button inverse>View On Map</Button>
            <Button to={`/places/${props.id}`}>Edit</Button>
            <Button danger>Delete</Button>
          </div>
        </Card>
      </li>
      }
    </React.Fragment>
  );
}

export default LocationItem;
