import React, {useEffect, useState, useContext} from "react"; ////////// check if userId === context.user ID
import { useParams, useLocation } from "react-router-dom";


import { AuthContext } from "../../shared/contex/auth-context"; /////////////
import LoadingSpinner from "../../shared/components/UiElements/LoadingSpinner";
import LocationList from "../components/location-list/LocationList";
import { useHttpClient } from "../../shared/hooks/http-hook";
import Card from "../../shared/components/UiElements/Card";
import Button from "../../shared/components/FormElements/Button";
import { AiOutlineArrowLeft,AiOutlinePlus } from 'react-icons/ai';


// const STATIC_PLACES = [
//     {
//         id: 'p1',
//         title: 'Empire State Building',
//         description: 'One of the most famous buildings in the world',
//         imageUrl: 'https://www.stockvault.net/data/2020/01/22/272769/preview16.jpg',
//         address: 'New York, NY 10001, USA',
//         coordinates: {
//             lat: 40.7485492,
//             lng: -73.9857635
//         },
//         creator: 'u1'
//     },
//     {
//         id: 'p2',
//         title: 'Burj Khalifa',
//         description: 'The tallest building in the world!',
//         imageUrl: 'https://embriture.org/wp-content/uploads/2021/09/Burj-portrait-lagre_tcm25-475749.jpg',
//         address: '1 Sheikh Mohammed bin Rashid Blvd - Downtown Dubai - Dubai - United Arab Emirates',
//         coordinates: {
//             lat: 25.1972018,
//             lng: 55.2721824
//         },
//         creator: 'u2'
//     },
//     {
//         id: 'p3',
//         title: 'Machu Picchu',
//         description: 'A large 15th-century Inca city',
//         imageUrl: 'https://images.pexels.com/photos/2929906/pexels-photo-2929906.jpeg',
//         address: 'Machu Picchu, Peru',
//         coordinates: {
//             lat: -13.1631412,
//             lng: -72.5471516
//         },
//         creator: 'u2'
//     },
//     {
//         id: 'p4',
//         title: 'The Dead Sea',
//         description: 'Lowest point on Earth, a salt-rich lake surrounded by desert beaches and mineral',
//         imageUrl: 'https://www.onthegotours.com/blog/wp-content/uploads/2019/03/Aerial-view-of-the-Dead-Sea.jpg',
//         address: 'Dead Sea, Israel',
//         coordinates: {
//             lat: 31.1635756,
//             lng: 35.3671583
//         },
//         creator: 'u3'
//     },
// ];


const UserLocations = () => {
    const auth = useContext(AuthContext);

    const [loadedPlaces, setLoadedPlaces] = useState();
    const {isLoading, error, sendRequest, clearError} = useHttpClient();

    const userId = useParams().userId;


    const [isHomePage, setHomePage] = useState(false);
  
    const currPath = useLocation();

    useEffect( () => {
        currPath.pathname === "/" ? setHomePage(true) : setHomePage(false);
        const fetchPlaces = async () => {
            try {
                const responseData = await sendRequest(
                    `http://localhost:5000/api/places/user/${userId}`
                );
                setLoadedPlaces(responseData.places);
            } catch (error) {}
        }
        fetchPlaces();
    }, [sendRequest, currPath]);

    const placeDeleteHandler = deletedPlaceId => {
        setLoadedPlaces(prevPlaces => prevPlaces.filter(place => place.id !== deletedPlaceId));
    }
  
    
    return (
      <React.Fragment>
        {!isHomePage && (
          <section>
            <Button inverse to="/users">
              <AiOutlineArrowLeft />
            </Button>
          </section>
        )}
        {!isHomePage && loadedPlaces && (
            
          <div className="center">
            <Button inverse to="places/new">
              <AiOutlinePlus /> Add New
            </Button>
          </div>
        )}

        {isLoading && (
          <div className="center">
            <LoadingSpinner />
          </div>
        )}
        {!isLoading && loadedPlaces && (
          <LocationList
            items={loadedPlaces}
            onDeletePlace={placeDeleteHandler}
          />
        )}

        {(auth.userId === userId && !isLoading && !loadedPlaces && (
          <div className="place-list center">
            <Card>
              <h2>No Location Found maybe create on?</h2>
              <Button to="/places/new"> Share a Place</Button>
            </Card>
          </div>
        )) ||
          (!isLoading && !loadedPlaces && (
            <div className="center">
              <Card>
                <h2>No Places Found For This User!</h2>
              </Card>
            </div>
          ))}
      </React.Fragment>
    ); 
}
export default UserLocations;
