import React, {useState, useContext} from "react";

import './Auth.css';
import Card from "../../../shared/components/UiElements/Card";
import Button from "../../../shared/components/FormElements/Button";
import Input from "../../../shared/components/FormElements/Input";
import { useForm } from "../../../shared/hooks/form-hook";
import { AuthContext } from "../../../shared/contex/auth-context";
import ErrorModal from "../../../shared/components/UiElements/ErrorModal";
import LoadingSpinner from "../../../shared/components/UiElements/LoadingSpinner";
import { VALIDATOR_EMAIL, VALIDATOR_MINLENGTH, VALIDATOR_REQUIRE } from "../../../shared/util/validators";


const Auth = () => {
    const auth = useContext(AuthContext);
    const [isLoginMode, setLoginMode] = useState(true);
    const [isLoading, setIsLoading] = useState(false);
    const [signInError, setError] = useState();

    const [formState, inputHandler, setFormData] = useForm({
        email: {
            value: '',
            isValid: false
        },
        password: {
            value: '',
            isValid: false
        }
    }, false);


    const authSubmitHandler = (event) => {
        event.preventDefault();

        const userEmail = formState.inputs.email.value;
        const userPassword = formState.inputs.password.value;
        
        setIsLoading(true);
        
        if(isLoginMode) {
            fetch('http://localhost:5000/api/users/login', {
              method: "POST",
              headers: {
                "content-Type": "application/json",
              },
              body: JSON.stringify({
                email: userEmail,
                password: userPassword,
              }),
            })
            .then((response) => { 
                const responseData = response.json();
                if(!response.ok) {
                    responseData.then(err => {
                        setError(err.message);
                        return new Error(err.message);
                    });
                }
                setIsLoading(false);
                auth.login();
            })
            .then((err) => {
                setIsLoading(false);
                setError(err || "Something went wrong!!");
                console.log(err);
            });
            
            
        } else {
            const userName = formState.inputs.name.value;
            fetch("http://localhost:5000/api/users/signup", {
              method: "POST",
              headers: {
                "content-Type": "application/json",
              },
              body: JSON.stringify({
                name: userName,
                email: userEmail,
                password: userPassword,
              }),
            })
            .then((response) => { 
                setIsLoading(false);
                const responseData = response.json();
                if(!response.ok) {
                    responseData.then(err => {
                        console.log(err.message);
                        setError(err.message || "Something went wrong!!");
                        throw new Error(err.message);
                    });
                }
                else{
                    auth.login();
                }
            });

        }
    }

    const onSwitchModeHandler = () => {
        if(!isLoginMode){
            setFormData(
            {
                ...formState.inputs,
                name: undefined
            }, 
            formState.inputs.email.isValid && formState.inputs.password.isValid);
        } else{
            setFormData(
            {
                ...formState.inputs,
                name: {
                    value: '',
                    isValid: false
                }
            }, false);
        }
        setLoginMode(prev => !prev);
    }

    const errorHandler = () => {
        setError(null);
    }

    return (
        <React.Fragment>
        <ErrorModal errors={signInError} onClear={errorHandler}/>
        <Card className="authentication">
            {isLoading && <LoadingSpinner asOverlay />}
            <h2> { isLoginMode ? "Login" : "Sign Up" } </h2><hr/>
            <form onSubmit={authSubmitHandler}>
                {!isLoginMode &&
                    <Input 
                        element="input"
                        type="text"
                        id="name"
                        label="Full Name"
                        validators={[VALIDATOR_REQUIRE()]}
                        errorText="Please enter your a name"
                        onInput={inputHandler}
                    />    
                }
                <Input 
                    element="input"
                    type='email'
                    id="email"
                    label="E-Mail"
                    validators={[VALIDATOR_EMAIL()]}
                    errorText="Please enter a valid email"
                    onInput={inputHandler}
                />
                <Input 
                    element="input"
                    id="password"
                    type="password"
                    label="Password"
                    validators={[VALIDATOR_MINLENGTH(6)]}
                    errorText="Please enter a valid password. min 6 characters"
                    onInput={inputHandler}
                />
                <Button disabled={!formState.isValid} type="submit">{ !isLoginMode? "Sign Up" : "Login" }</Button>
            </form>
            <Button inverse onClick={onSwitchModeHandler}>Switch to { isLoginMode? "Sign Up" : "Login" }</Button>
        </Card>
        </React.Fragment>
    );
}

export default Auth;
