import React, { useState, useCallback, useEffect} from "react";
import {
  BrowserRouter as Router,
  Navigate,
  Route,
  Routes,
} from "react-router-dom";

import MainNavigation from "./shared/components/Navigation/MainNavigation";
import UserLocations from "./locations/pages/UserLocations";
import { AuthContext } from "./shared/contex/auth-context";
import EditLocation from "./locations/pages/EditLocation";
import NewLocation from "./locations/pages/NewLocation";
import Auth from "./users/pages/Auth/Auth";
import Users from "./users/pages/Users";
import Home from "./shared/Home/Home";
import "./App.css";

let logoutTimer;

const App = () => {

  const [token, setToken] = useState(false);
  const [userId, setUserId] = useState();
  const [expirationDateState, setExpirationDateState] = useState()



  const login = useCallback((uid, token, expirationDte) => {
    setToken(token);
    setUserId(uid);
    const tokenExpiration = expirationDte || new Date(new Date.getTime() + 1000 * 60 * 60);
    setExpirationDateState(tokenExpiration);
    localStorage.setItem(
      "userData", 
      JSON.stringify({
        userId: uid,
        token: token,
        expiration: tokenExpiration.toISOString()
      })
    );
  }, []);

  const logout = useCallback(() => {
    setToken(null);
    setUserId(null);
    setExpirationDateState(null);
    localStorage.removeItem("userData");
  }, []);


  useEffect(() => {
    if(token && expirationDateState) {
      const timeLeft = expirationDateState.getTime() - new Date().getTime();
      logoutTimer = setTimeout(logout, timeLeft);
    } else {
      clearTimeout(logoutTimer);
    }
    }, [token, logout, expirationDateState]
  );


  useEffect(() => {
    const storedData = JSON.parse(localStorage.getItem("userData"));
    if(storedData && storedData.token && new Date(storedData.expiration) > new Date() ) {
      login(storedData.userId, storedData.token, new Date(storedData.expiration));
    }
    }, [login]
  );

  let routes;

  if (token) {
    routes = (
      <Routes>
        <Route path="/"  element={<Home />}></Route>
        <Route path="/users"  element={<Users />}></Route>
        <Route path="/places/new" element={<NewLocation />} />
        <Route path="/:userId/places" element={<UserLocations />} />
        <Route path="/places/:placeId" element={<EditLocation />} />
        <Route path="*" element={<Navigate to="/users" />}></Route>
      </Routes>
    );
  }else {
    routes = (
      <Routes>
        <Route path="/" element={<Home />} /> 
        <Route path="/users"  element={<Users />}></Route>
        <Route path="/auth" element={<Auth />} />
        <Route path="/:userId/places" element={<UserLocations />} />
        <Route path="*" element={<Navigate to="/auth" />}></Route>
      </Routes>
    );
  }

  return (
    <AuthContext.Provider 
      value={{
        isLoggedIn: !!token, 
        token: token,
        userId: userId, 
        login: login, 
        logout: logout
      }}
    >
      <Router>
        <MainNavigation />
        <main>
          {routes}
        </main>
      </Router>
    </AuthContext.Provider>
  );
};

export default App;
