import React from "react";
import { useParams } from "react-router-dom";
import Input from "../../shared/components/FormElements/Input";
import Button from "../../shared/components/FormElements/Button";
import { VALIDATOR_REQUIRE, VALIDATOR_MINLENGTH } from "../../shared/util/validators";
import "./LocationForm.css";

const STATIC_PLACES = [
    {
        id: 'p1',
        title: 'Empire State Building',
        description: 'One of the most famous buildings in the world',
        imageUrl: 'https://embriture.org/wp-content/uploads/2021/09/Burj-portrait-lagre_tcm25-475749.jpg',
        address: '1 Sheikh Mohammed bin Rashid Blvd - Downtown Dubai - Dubai - United Arab Emirates',
        coordinates: {
            lat: 25.1972018,
            lng: 55.2721824
        },
        creator: 'u1'
    },
    {
        id: 'p2',
        title: 'Empire State Building',
        description: 'One of the most famous buildings in the world',
        imageUrl: 'https://embriture.org/wp-content/uploads/2021/09/Burj-portrait-lagre_tcm25-475749.jpg',
        address: '1 Sheikh Mohammed bin Rashid Blvd - Downtown Dubai - Dubai - United Arab Emirates',
        coordinates: {
            lat: 25.1972018,
            lng: 55.2721824
        },
        creator: 'u2'
    },
    {
        id: 'p3',
        title: 'Empire State Building',
        description: 'One of the most famous buildings in the world',
        imageUrl: 'https://embriture.org/wp-content/uploads/2021/09/Burj-portrait-lagre_tcm25-475749.jpg',
        address: '1 Sheikh Mohammed bin Rashid Blvd - Downtown Dubai - Dubai - United Arab Emirates',
        coordinates: {
            lat: 25.1972018,
            lng: 55.2721824
        },
        creator: 'u2'
    },
];

const EditLocation = () => {
    const placeId = useParams().placeId;
    const identifiedLocation = STATIC_PLACES.find(p => p.id === placeId);

    if(!identifiedLocation){
        return <h2>Could not find the location </h2>;
    }

    return (
        <form className="place-form">
            <Input 
                id="title"
                element="input"
                type="text"
                label="Title"
                validators={[VALIDATOR_REQUIRE()]}
                errorText="Enter a valid title"
                onInput={() => {}}
                value={identifiedLocation.title}
                valid={true} 
            />
            <Input 
                id="description"
                element="textarea"
                label="Description"
                validators={[VALIDATOR_MINLENGTH(5)]}
                errorText="Enter a valid description! min 5 characters"
                onInput={() => {}}
                value={identifiedLocation.description}
                valid={true} 
            />
            <Button type="submit" disabled={true}>Update Location</Button>
        </form>
    );
}

export default EditLocation;
